import React, { useState } from "react";
import "../CSS/contact.css"; // Adjust the path to your CSS file as necessary
// import contactImage from "../images/contact.jpg";
const Contact = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });
  // Add state for notification card visibility and message
  const [notification, setNotification] = useState({
    isVisible: false,
    message: "",
  });
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Replace 'http://localhost:5000' with your server's URL if different
      const response = await fetch("http://localhost:5000/api/contact", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      // Set notification state to show success message
      setNotification({
        isVisible: true,
        message: "Thank you for contacting us! We will get back to you soon.",
      });

      // Reset form data and hide notification after a delay
      setTimeout(() => {
        setNotification({ isVisible: false, message: "" });
        setFormData({
          name: "",
          email: "",
          message: "",
        });
      }, 3000); // Hides the notification card after 3 seconds
    } catch (error) {
      // ... existing error handling logic
      setNotification({
        isVisible: true,
        message: "There was an error submitting the form.",
      });
      setTimeout(() => {
        setNotification({ isVisible: false, message: "" });
      }, 3000); // Hides the notification card after 3 seconds
    }
  };

  return (
    <>
      <div className="contact-container">
        <div className="contact-header">
          <h2>Contact Us</h2>
          <p>If you have any questions, please feel free to reach out to us!</p>
        </div>

        <div className="contact-content">
          <div className="contact-card">
            <h3>Contact Card</h3>
            <p>
              <strong>Company Name</strong>
            </p>
            <p>HBR Layout,</p>
            <p>Bangalore, Karnataka, 560043</p>
            <p>Phone: 9527659939</p>
            <p>Email: 2024aaras@gmail.com</p>
          </div>

          <div className="contact-form">
            <h3>Do Contact Us ✌️ </h3>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                name="name"
                className="form-input"
                value={formData.name}
                onChange={handleChange}
                placeholder="Your Name"
                required
              />
              <input
                type="email"
                name="email"
                className="form-input"
                value={formData.email}
                onChange={handleChange}
                placeholder="Your Email"
                required
              />
              <textarea
                name="message"
                className="form-textarea"
                value={formData.message}
                onChange={handleChange}
                placeholder="Your Message"
                required
              />
              <button type="submit" className="form-button">
                Send Message
              </button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default Contact;
