import React from "react";
import "../CSS/Recipes.css";


const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically handle form submission, for example by sending the data to an API
    alert('Form submitted! Check the console for the form data.');
    console.log(formData);
    // Reset the form data
    setFormData({
      name: '',
      email: '',
      message: '',
    });
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column', marginTop: '50px' }}>
      <div style={{ marginBottom: '30px', textAlign: 'center' }}>
        <h2>Contact Us</h2>
        <p>If you have any questions, please feel free to reach out to us!</p>
      </div>

      <div style={{ display: 'flex', justifyContent: 'space-between', width: '100%', maxWidth: '600px' }}>
        <div style={{ width: '48%' }}>
          <h3>Contact Card</h3>
          <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '5px' }}>
            <p><strong>Company Name</strong></p>
            <p>123 Business Ave</p>
            <p>City, State, 12345</p>
            <p>Phone: (123) 456-7890</p>
            <p>Email: contact@example.com</p>
          </div>
        </div>

        <div style={{ width: '48%' }}>
          <h3>Contact Form</h3>
          <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Your Name"
              required
            />
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Your Email"
              required
            />
            <textarea
              name="message"
              value={formData.message}
              onChange={handleChange}
              placeholder="Your Message"
              required
            />
            <button type="submit">Send Message</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Contact;
