// Recipes.js

import React from "react";
import "../CSS/Recipes.css"; // Ensure the CSS file path is correct

// Array of recipes with their details
const recipes = [
  {
    title: "Chole - ",
    imageUrl: "/path-to-image-1.jpg", // Replace with the path to your image
    description:
      "This is a great recipe for a delicious meal that you can make quickly and easily at home.",
  },
  // ... more recipe objects
];

const Recipes = () => {
  return (
    <div className="recipes-gallery">
      {recipes.map((recipe, index) => (
        <div key={index} className="recipe-item">
          <div className="recipe-thumbnail">
            <img
              src={recipe.imageUrl}
              alt={recipe.title}
              className="recipe-photo"
            />
          </div>
          <div className="recipe-info">
            <h3 className="recipe-name">{recipe.title}</h3>
            <p className="recipe-description">{recipe.description}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Recipes;
