// Recipes.js

import React from "react";
import "../CSS/Recipes.css"; // Ensure the CSS file path is correct

// Array of YouTube Shorts URLs
const shortsUrls = [
  "https://youtube.com/shorts/twr4ONRPGJA?si=yTL0YPSMb_xC46X7",
  // ... more YouTube Shorts URLs
];

const Recipes = () => {
  return (
    <div className="recipes-container">
      {shortsUrls.map((shortsUrl, index) => (
        <div key={index} className="recipe-card">
          {/* Thumbnail or a placeholder for the Shorts video */}
          <div className="shorts-thumbnail">
            {/* You can place an image here */}
          </div>
          {/* Link to the YouTube Shorts video */}
          <a href={shortsUrl} target="_blank" rel="noopener noreferrer" className="shorts-link">
  Watch on YouTube Shorts
</a>
        </div>
      ))}
    </div>
  );
};

export default Recipes;
