import React, { useState } from "react";
import "../CSS/contact.css"; // Adjust the path to your CSS file as necessary

const Contact = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically handle form submission, for example by sending the data to an API
    alert("Form submitted! Check the console for the form data.");
    console.log(formData);
    // Reset the form data
    setFormData({
      name: "",
      email: "",
      message: "",
    });
  };

  return (
    <div className="contact-container">
      <div className="contact-header">
        <h2>Contact Us</h2>
        <p>If you have any questions, please feel free to reach out to us!</p>
      </div>

      <div className="contact-content">
        <div className="contact-card">
          <h3>Contact Card</h3>
          <p>
            <strong>Company Name</strong>
          </p>
          <p>123 Business Ave</p>
          <p>City, State, 12345</p>
          <p>Phone: (123) 456-7890</p>
          <p>Email: contact@example.com</p>
        </div>

        <div className="contact-form">
          <h3>Contact Form</h3>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="name"
              className="form-input"
              value={formData.name}
              onChange={handleChange}
              placeholder="Your Name"
              required
            />
            <input
              type="email"
              name="email"
              className="form-input"
              value={formData.email}
              onChange={handleChange}
              placeholder="Your Email"
              required
            />
            <textarea
              name="message"
              className="form-textarea"
              value={formData.message}
              onChange={handleChange}
              placeholder="Your Message"
              required
            />
            <button type="submit" className="form-button">
              Send Message
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Contact;
