import React from "react";

// Component to display a single YouTube Short iframe
const YouTubeShortIframe = ({ videoId }) => {
  const src = `https://www.youtube.com/embed/${videoId}`;
  return (
    <iframe
      src={src}
      frameBorder="0"
      allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
      allowFullScreen
      title={`YouTube Short - ${videoId}`}
      style={{
        width: "100%", // Responsive width
        height: "315px", // Fixed height, you can also make it responsive
      }}
    ></iframe>
  );
};

// Main component to display all YouTube Shorts
const YouTubeShortsDisplay = ({ videoIds }) => {
  return (
    <div>
      {videoIds.map((videoId) => (
        <div key={videoId} style={{ marginBottom: "1rem" }}>
          {" "}
          {/* Add some space between iframes */}
          <YouTubeShortIframe videoId={videoId} />
        </div>
      ))}
    </div>
  );
};

export default YouTubeShortsDisplay;
