import React, { useState } from "react";
import "../CSS/contact.css"; // Ensure this path is correct

const Contact = () => {
  // Form data state
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  // Notification state
  const [notification, setNotification] = useState({
    isVisible: false,
    message: "Message s",
  });

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Submit form data to the backend API
      const response = await fetch("/api/contact", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      // Check if the submission was successful
      if (response.ok) {
        // Display success notification
        setNotification({
          isVisible: true,
          message: "Thank you for contacting us! We will get back to you soon.",
        });

        // Hide notification after 3 seconds and reset form
        setTimeout(() => {
          setNotification({ isVisible: false, message: "" });
          setFormData({
            name: "",
            email: "",
            message: "",
          });
        }, 3000);
      } else {
        // If the server response was not ok, inform the user
        const errorData = await response.json();
        throw new Error(
          errorData.message || "There was an error submitting the form."
        );
      }
    } catch (error) {
      // Handle submission error
      setNotification({
        isVisible: true,
        message: error.message || "There was an error submitting the form.",
      });
      // Hide notification after 3 seconds
      setTimeout(() => {
        setNotification({ isVisible: false, message: "" });
      }, 3000);
    }
  };

  // JSX for the contact component
  return (
    <div className="contact-container">
      {/* ... rest of your component */}
      <div className="contact-form">
        <h3>Do Contact Us ✌️</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            className="form-input"
            value={formData.name}
            onChange={handleChange}
            placeholder="Your Name"
            required
          />
          <input
            type="email"
            name="email"
            className="form-input"
            value={formData.email}
            onChange={handleChange}
            placeholder="Your Email"
            required
          />
          <textarea
            name="message"
            className="form-textarea"
            value={formData.message}
            onChange={handleChange}
            placeholder="Your Message"
            required
          />
          <button type="submit" className="form-button">
            Send Message
          </button>
        </form>
      </div>
      {/* Notification card */}
      {notification.isVisible && (
        <div className="notification-card">
          <p>{notification.message}</p>
        </div>
      )}
    </div>
  );
};

export default Contact;
