// Recipes.js

import React from "react";
import "../CSS/Recipes.css"; // Ensure the CSS file path is correct

// Array of recipes with their details
const recipes = [
  {
    title: "Recipe Title 1",
    imageUrl: "/path-to-image-1.jpg", // Replace with the path to your image
    shortsUrl: "https://youtube.com/shorts/bzYttrxG4eg?feature=share"
  },
  // ... more recipe objects
];

const Recipes = () => {
  return (
    <div className="recipes-container">
      {recipes.map((recipe, index) => (
        <div key={index} className="recipe-card">
          <img src={recipe.imageUrl} alt={recipe.title} className="recipe-image"/>
          <h3>{recipe.title}</h3>
          {/* Link to the YouTube Shorts video */}
          <a href={recipe.shortsUrl} target="_blank" rel="noopener noreferrer" className="shorts-link">
            Watch on YouTube Shorts
          </a>
        </div>
      ))}
    </div>
  );
};

export default Recipes;
